(146, 1, 'West Bengal ', 0, 0, 1, '0', '', '2024-07-30', 'West Bengal is a state in eastern India, between the Himalayas and the Bay of Bengal.', 'WestBengal.avif', '', 'West Bengal Tour Packages', ''),
(147, 1, 'Karnataka', 0, 0, 1, '0', '', '2024-07-30', 'Karnataka is a state in southwest India with Arabian Sea coastlines. The capital, Bengaluru (formerly Bangalore), is a high-tech hub known for its shopping and nightlife.', 'Karnataka.avif', '', 'Karnataka Tour Packages', ''),
